package fa.training.assignment1;

public class RectangleExercise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double rong=5.5;
		double cao=8.5;
		double dt=cao * rong;
		double cv=2*(cao + rong);
		System.out.println("Diện tích là "+rong+" * "+cao+" = "+dt);
		System.out.println("Chu vi là "+2+" * ("+rong+" + "+cao+") = "+cv);
	}

}
